  render: () => <div className="max-w-4xl mx-auto space-y-8 p-6">
      <div className="text-center mb-8">
        <h1 className="text-white text-2xl font-bold mb-2">
          Toast Components Showcase
        </h1>
        <p className="text-gray-400 text-sm">
          All specialized toast components rendered persistently
        </p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Achievement Toasts */}
        <div className="space-y-4">
          <h3 className="text-white text-lg font-semibold border-b border-gray-600 pb-2">
            Achievement Toasts
          </h3>
          <div className="space-y-3">
            <div>
              <p className="text-gray-400 text-xs mb-2">
                Standard Achievement (66.7% progress)
              </p>
              <AchievementToast title="Pacifist Path" subtitle="Earned!" xpAmount={100} progress={66.7} isDraft={false} showClose={false} duration={0} />
            </div>

            <div>
              <p className="text-gray-400 text-xs mb-2">
                Draft Achievement (16.7% progress)
              </p>
              <AchievementToast title="Diamonds" subtitle="Earned!" xpAmount={100} progress={16.7} isDraft={true} showClose={false} duration={0} />
            </div>

            <div>
              <p className="text-gray-400 text-xs mb-2">
                Completed Achievement (100% progress)
              </p>
              <AchievementToast title="Master Strategist" subtitle="Completed!" xpAmount={250} progress={100} isDraft={false} showClose={false} duration={0} />
            </div>
          </div>
        </div>

        {/* Network Switch Toasts */}
        <div className="space-y-4">
          <h3 className="text-white text-lg font-semibold border-b border-gray-600 pb-2">
            Network Switch Toasts
          </h3>
          <div className="space-y-3">
            <div>
              <p className="text-gray-400 text-xs mb-2">Starknet Mainnet</p>
              <NetworkSwitchToast networkName="Starknet Mainnet" networkIcon={<StarknetIcon size="default" />} showClose={false} duration={0} />
            </div>

            <div>
              <p className="text-gray-400 text-xs mb-2">Custom Network</p>
              <NetworkSwitchToast networkName="Nums Chain" networkIcon={<div className="w-6 h-6 bg-purple-500 rounded-full flex items-center justify-center text-white text-xs font-bold">
                    N
                  </div>} showClose={false} duration={0} />
            </div>

            <div>
              <p className="text-gray-400 text-xs mb-2">Ethereum Mainnet</p>
              <NetworkSwitchToast networkName="Ethereum Mainnet" networkIcon={<div className="w-6 h-6 bg-blue-500 rounded-full flex items-center justify-center text-white text-xs font-bold">
                    E
                  </div>} showClose={false} duration={0} />
            </div>
          </div>
        </div>

        {/* Error Toasts */}
        <div className="space-y-4">
          <h3 className="text-white text-lg font-semibold border-b border-gray-600 pb-2">
            Error Toasts
          </h3>
          <div className="space-y-3">
            <div>
              <p className="text-gray-400 text-xs mb-2">
                Execution Error (66.7% progress)
              </p>
              <ErrorToast message="Execution Error" progress={66.7} showClose={false} duration={0} />
            </div>

            <div>
              <p className="text-gray-400 text-xs mb-2">
                Network Timeout (30% progress)
              </p>
              <ErrorToast message="Network Timeout" progress={30} showClose={false} duration={0} />
            </div>

            <div>
              <p className="text-gray-400 text-xs mb-2">
                Transaction Failed (10% progress)
              </p>
              <ErrorToast message="Transaction Failed" progress={10} showClose={false} duration={0} />
            </div>
          </div>
        </div>

        {/* Transaction Notifications */}
        <div className="space-y-4">
          <h3 className="text-white text-lg font-semibold border-b border-gray-600 pb-2">
            Transaction Notifications
          </h3>
          <div className="space-y-3">
            <div>
              <p className="text-gray-400 text-xs mb-2">
                Confirming Transaction (66.7% progress)
              </p>
              <TransactionToast status="confirming" isExpanded={true} label="New Game" progress={66.7} showClose={false} duration={0} />
            </div>

            <div>
              <p className="text-gray-400 text-xs mb-2">
                Confirmed Transaction (100% progress)
              </p>
              <TransactionToast status="confirmed" isExpanded={true} label="Token Swap" progress={100} showClose={false} duration={0} />
            </div>

            <div>
              <p className="text-gray-400 text-xs mb-2">Collapsed States</p>
              <div className="flex gap-2">
                <div>
                  <p className="text-gray-400 text-xs mb-1">Confirming</p>
                  <TransactionToast status="confirming" isExpanded={false} showClose={false} duration={0} />
                </div>
                <div>
                  <p className="text-gray-400 text-xs mb-1">Confirmed</p>
                  <TransactionToast status="confirmed" isExpanded={false} showClose={false} duration={0} />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
