  render: () => <div className="max-w-3xl mx-auto space-y-6 p-6">
      <div className="text-center mb-6">
        <h2 className="text-white text-xl font-bold mb-2">
          Duration Guidelines
        </h2>
        <p className="text-gray-400 text-sm">
          Recommended duration settings for different toast types
        </p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="space-y-4">
          <h3 className="text-white text-lg font-semibold">
            Quick Notifications
          </h3>
          <p className="text-gray-400 text-sm mb-3">
            2-3 seconds • Simple confirmations
          </p>

          <div className="space-y-2">
            <div>
              <p className="text-gray-400 text-xs mb-1">Network Switch (3s)</p>
              <NetworkSwitchToast networkName="Quick Switch" showClose={false} duration={0} />
            </div>

            <div>
              <p className="text-gray-400 text-xs mb-1">
                Confirmed Transaction (3s)
              </p>
              <TransactionToast status="confirmed" isExpanded={true} label="Quick Action" progress={100} showClose={false} duration={0} />
            </div>
          </div>
        </div>

        <div className="space-y-4">
          <h3 className="text-white text-lg font-semibold">
            Standard Notifications
          </h3>
          <p className="text-gray-400 text-sm mb-3">
            4-6 seconds • Achievements, progress
          </p>

          <div className="space-y-2">
            <div>
              <p className="text-gray-400 text-xs mb-1">Achievement (4s)</p>
              <AchievementToast title="Standard Achievement" subtitle="Earned!" xpAmount={100} progress={75} isDraft={false} showClose={false} duration={0} />
            </div>

            <div>
              <p className="text-gray-400 text-xs mb-1">Error (5s)</p>
              <ErrorToast message="Standard Error" progress={50} showClose={false} duration={0} />
            </div>
          </div>
        </div>

        <div className="space-y-4">
          <h3 className="text-white text-lg font-semibold">
            Important Notifications
          </h3>
          <p className="text-gray-400 text-sm mb-3">
            6-10 seconds • Critical errors, long processes
          </p>

          <div className="space-y-2">
            <div>
              <p className="text-gray-400 text-xs mb-1">
                Confirming Transaction (8s)
              </p>
              <TransactionToast status="confirming" isExpanded={true} label="Important Process" progress={40} showClose={false} duration={0} />
            </div>

            <div>
              <p className="text-gray-400 text-xs mb-1">Critical Error (10s)</p>
              <ErrorToast message="Critical System Error" progress={20} showClose={false} duration={0} />
            </div>
          </div>
        </div>

        <div className="space-y-4">
          <h3 className="text-white text-lg font-semibold">
            Persistent Toasts
          </h3>
          <p className="text-gray-400 text-sm mb-3">
            Manual dismiss • Requires user action
          </p>

          <div className="space-y-2">
            <div>
              <p className="text-gray-400 text-xs mb-1">Manual Dismiss Only</p>
              <AchievementToast title="Persistent Achievement" subtitle="Click to dismiss" xpAmount={500} progress={100} isDraft={false} showClose={false} duration={0} />
            </div>
          </div>
        </div>
      </div>
    </div>
